    function agendaDecisionContent() {
        var agendaContentHeight = 327;

        if($("#agenda-decision-list-item").hasClass("in")){

        } else if ($(window).width() > 767) {

            var agendaHeaderHeight = $('#agenda-decision-sm-nav-tabs').outerHeight(true);
            if ($(window).width() > 975) {
                agendaHeaderHeight = 0; //$('#agenda-decision-list-item-header').outerHeight(true);
            }
            var widgetHeight = parseInt($('.tabbed-widget-content-wrapper').css('min-height'), 10);
            var agendaFooterHeight = $('#agenda-decision-footer').outerHeight();

            agendaContentHeight = widgetHeight - agendaHeaderHeight - agendaFooterHeight;

            $('#agenda-decision-list-item').css('min-height', agendaContentHeight);
            $('#agenda-decision-list-item').height(agendaContentHeight);
        }

        if ($("#agenda-list-item").height() > agendaContentHeight || $("#decision-list-item").height() > agendaContentHeight) {
            $("#agenda-decision-list-item-show-more").removeClass("invisible");
        };
    }

    $(document).ready(function () {
        $("#earlier-meetings-carousel").carousel('pause')
        $("#ongoing-meetings-carousel").carousel('pause')
        $("#later-meetings-carousel").carousel('pause')
    });

    $('.carousel').on('slid.bs.carousel', function () {

        var carouselData = $(this).data('bs.carousel');
        var currentIndex = carouselData.getItemIndex(carouselData.$element.find('.item.active'));
        var total = carouselData.$items.length;
        var meetingLabel = 'Meeting';
        var slideCountText = meetingLabel + ' ' + (currentIndex + 1) + ' of ' + total;

        if (total > 0) {
            carouselData.$element.find('.meeting-number-of').text(slideCountText);
        } else {
            carouselData.$element.find('.meeting-number-of').text('');
        }
    });

    // Store the currently selected tab in the hash value.
    $("#tabbed-widget-tab-list > li > a").on("shown.bs.tab", function (e) {

        var id = $(e.target).attr("href").substr(1);
        id = id.replace('tabbed-widget-', 'pw-');

        var hash = window.location.hash;

        if (hash != '') {
            hash = hash.substr(1);

            var items = hash.split(',');
            var regex = new RegExp('^pw-', "i");
            var itemFound = false;

            // Replace an existing value in the hash for the find tab control or add it if not present.
            for (i = 0; i < items.length; i++) {
                if (regex.exec(items[i]) !== null) {
                    items[i] = id;
                    itemFound = true;
                    break;
                }
            }
            if (!itemFound) {
                items.push(id);
            }

            hash = items.join();
        }
        else {
            hash = id;
        }

        window.location.hash = hash;

    });


    // On load of the page: switch to the currently selected tab.
    $(document).ready(function () {

        var hash = window.location.hash;

        if (hash != '') {

            // Remove the # character that comes at the start of the returned hash value.
            hash = hash.substr(1);

            // As many different tab control ids can be contained in the hash value, find the
            // one for the parliament work tab control and show that tab.
            var items = hash.split(',');
            var item = '';
            var regex = new RegExp('^pw-', "i");

            for (i = 0; i < items.length; i++) {
                if (regex.exec(items[i]) !== null) {
                    item = items[i];
                    break;
                }
            }
            if (item != '') {
                $('#tabbed-widget-tab-list a[href="#' + item.replace('pw-', 'tabbed-widget-') + '"]').tab('show');
            }
        }
    });
    


