    $(document).ready(function () {
        var newsEventContentWrapperHeight = $(".news-event-content-wrapper").height();

        newsEventShowHideMore();

        //Remove auto slide
        $('#news-events-carousel').carousel({
            interval: false
        });

        $('#news-events-carousel').bind('slid.bs.carousel', function (e) {
            newsEventShowHideMore(e);

            // Hide non-active slides from screen readers.
            $("[id^='news-events-slide-'][class*=active]").removeAttr("aria-hidden");
            $("[id^='news-events-slide-']").not("[class*='active']").attr("aria-hidden", "true");
        });

        // Display show more button if the news is bigger than the container on slide finish
        function newsEventShowHideMore(e) {
            var newsEventActiveSlideContentHeight = $("#news-events-carousel > .carousel-inner .active .news-event-content").height();
            if (newsEventContentWrapperHeight < newsEventActiveSlideContentHeight+10) {
                $("#news-events-carousel .active .news-event-show-more").removeClass("invisible");
            }
        }

        $(".news-event-show-more-link").click(function (e) {
            if ($("#news-events-carousel .active .news-event-content-wrapper").hasClass("in")) {
                $("#news-events-carousel .active .news-event-show-more-link").html("Show more ");
            }
            else {
                $("#news-events-carousel .active .news-event-show-more-link").html("Show less ");
            }
        })
    });

body.script:
        $(function () {
            $('[data-toggle="tooltip"]').tooltip({ delay: { "show": 300, "hide": 50 }, placement: "top" })
        })


        $(function () {

            var resizeTimer;

            // For accessibility purposes, only inlude the aria-expanded attribute when the
            // accordian panels appear collapsable.
            function checkWindowSize() {
                if (window.matchMedia('(max-width: 767px)').matches) {
                    $('.widget-accordion-panel-heading:not([aria-expanded])').each(function () {
                        var controlState = $('#' + $(this).attr('aria-controls')).attr('aria-expanded');
                        if (controlState == null) { controlState = false; }
                        $(this).attr('aria-expanded', controlState);
                    });
                } else {
                    $('.widget-accordion-panel-heading[aria-expanded]').removeAttr('aria-expanded');
                }
            }

            $(window).resize(function () {
                // For performance reasons, throttle the firing of the resize event
                // so that it doesn't fire as often.
                clearTimeout(resizeTimer);
                resizeTimer = setTimeout(checkWindowSize, 250);
            });

            checkWindowSize();
        })
